INSERT INTO "quiz_question" VALUES(502,'In termini di sistemi operativi, cosa significa BSD?','OS',3,'','BSD è un derivato del sistema operativo Unix.',1,'2017-05-12 02:35:31.411523','2017-05-12 02:35:31.411531');
